 if (window.addEventListener){
    window.addEventListener('load',LoadSimply, false);
 } else if (window.attachEvent){
      window.attachEvent("onload",LoadSimply);
 } else {
      setTimeout("LoadSimply()",2000);
 }

 function LoadSimply() {
    if (typeof(MysLiveCekLoaded) == "undefined") {
       MysLiveCekLoaded=1;

       var ns=document.createElement('script');
       ns.src="http://myslivecek.simply.com/simply.js?code="+code;
       document.body.appendChild(ns);

       var fr=document.createElement('iframe');
       fr.setAttribute('src','http://bh.simply.com/magellan.html?code='+code+'&amp;from='+encodeURIComponent(document.referrer));
       fr.style.width = "0px";
       fr.style.height = "0px";
       fr.frameBorder = "0";
       fr.scrolling="no";
       document.body.appendChild(fr);

    }
}

