function controllaAdv(item){
	var contieneA = $(item+ ' a').length;
	var contieneObj = $(item+ ' object').length;
	var contieneEmbed = $(item+ ' embed').length;
	var contieneScript = $(item+ ' script').length;
	var contieneDiv = $(item+ 'div:not(.centra_adv)').length;
	var contieneImg = $(item+ ' img').length;
	var contienecentraDiv = $(item+ '.centra_adv div object').length;

	if (!(contienecentraDiv+contieneA+contieneObj+contieneDiv+contieneImg+contieneEmbed)) {
		if (contieneScript<2){
			$(item).hide();
		}
	}
	else if($(item).height() < 15){
			$(item).hide();
	}
	else{
/*	$('.wide-pushbar').css('border','1px solid #C5C4C4');
*/	$('.wide-pushbar').css('min-height','45px');

	}
}
$(document).ready(function(){
	if ($('.wide-pushbar').length) {controllaAdv('.wide-pushbar');}
});

